package discovery;

import com.fasterxml.jackson.core.JsonFactory;
import com.fasterxml.jackson.core.type.TypeReference;
import com.fasterxml.jackson.databind.ObjectMapper;
import io.restassured.path.json.JsonPath;
import io.restassured.response.Response;
import io.restassured.response.ResponseBody;
import org.junit.platform.engine.support.descriptor.MethodSource;
import org.junit.platform.launcher.*;
import org.junit.platform.launcher.core.LauncherDiscoveryRequestBuilder;
import org.junit.platform.launcher.core.LauncherFactory;
import top.testeru.entity.TestCase;

import java.io.File;
import java.io.IOException;
import java.util.*;
import java.util.stream.Collectors;

import static io.restassured.RestAssured.given;
import static org.junit.platform.engine.discovery.ClassNameFilter.includeClassNamePatterns;
import static org.junit.platform.engine.discovery.DiscoverySelectors.selectPackage;
import static top.testeru.base.An_Base.logger;

/**
 * @author www.testeru.top
 * @version 1.0.0
 * @Project junit5-tutorial
 * @Description
 * @createTime 2022Âπ¥11Êúà30Êó• 15:07:00
 */
public class Junit5Demo_5_1Launcher {
    public static void main(String[] args) throws IOException {
        //ÂΩìÂâçÈ°πÁõÆÂú∞ÂùÄ
        logger.info("ÂΩìÂâçÈ°πÁõÆË∑ØÂæÑ‰∏∫Ôºö{}",System.getProperty("user.dir"));
        File file = new File(System.getProperty("user.dir"));
        List<TestCase> testCaseList = new ArrayList<>();

        LauncherDiscoveryRequest request = LauncherDiscoveryRequestBuilder.request()
                .selectors(
                        //ÈÄöËøáÂØπÂåÖ„ÄÅÁ±ª„ÄÅÊñπÊ≥ïÁª¥Â∫¶ÁöÑÈÄâÊã©Âô®ÔºåÂà∂ÂÆöË¶ÅÊâßË°åÁöÑËÑöÊú¨
                        selectPackage("top.testeru")
//                    selectClass(ClassATest.class)
//                    selectMethod("examples.packageA.ClassATest#testCaseA")
                ).filters(
                        //ÈÄöËøáÂØπÁ±ªÂêçÁöÑÈÄöÈÖçÁ¨¶ÂåπÈÖçÔºåÂà∂ÂÆöÊàñËÄÖÊéíÈô§Áõ∏ÂÖ≥Áî®‰æã
//                      excludeClassNamePatterns(".*ATest")
                        includeClassNamePatterns(".*Test")
                ).build();


        try (LauncherSession session = LauncherFactory.openSession()) {
            TestPlan testPlan = session.getLauncher().discover(request);


            System.out.println(testPlan);
            System.out.println(testPlan.getRoots());
            System.out.println();
//                    testPlan.getRoots().stream()
//                            .filter(testIdentifier -> testIdentifier.getDisplayName().contains("JUnit Jupiter"))
//                            .
            TestIdentifier testIdentifier1 = Optional.ofNullable(testPlan)
                            .get()
                            .getRoots()
                            .stream()
                            .filter(testIdentifier -> testIdentifier.getDisplayName().contains("JUnit Jupiter"))
                    .collect(Collectors.toList()).get(0);

//            TestIdentifier testIdentifier = testPlan.getRoots().stream().filter(
//                    testIdentifier1 -> testIdentifier1.getDisplayName().contains("JUnit Jupiter")
//            );
                    //

//            System.out.println(testPlan.getChildren());

            HashSet<TestIdentifier> testIdentifiers = Optional.ofNullable(testPlan)
                                                        .map(tp -> tp.getDescendants(testIdentifier1))
                                                        .orElseGet(Collections::emptySet)
                                                        .stream()
                                                        .filter(TestIdentifier::isTest)
                                    //                    .flatMap(this::getTest)
                                    //                    .ifPresent(Allure.getLifecycle()::setCurrentTestCase);
                                                        .collect(Collectors.toCollection(HashSet::new));
            System.out.println(testIdentifiers);
            testIdentifiers.forEach(testIdentifier -> {
                TestCase testCase = new TestCase();
                MethodSource methodSource = (MethodSource)testIdentifier.getSource().get();
                String className = methodSource.getClassName();
                String methodName = methodSource.getMethodName();
                System.out.println("className:"+className);
                System.out.println("methodName:"+methodName);
                testCase.setCaseTitle(className + "#" + methodName);
                testCase.setRemark(file.getPath());
                testCaseList.add(testCase);
            });
        } catch (Exception e) {
            e.printStackTrace();
        }
        //testcaseListÂÜôÂÖ•yamlÊñá‰ª∂

        ObjectMapper objectMapper = new ObjectMapper(new JsonFactory());
        //ÂÆû‰ΩìÁ±ªÁõ¥Êé•ËΩ¨‰∏∫string
        String testCaseListStr = objectMapper.writeValueAsString(testCaseList);
        System.out.println("carAsString:"+testCaseListStr);
        objectMapper.writeValue(new File("testcase.json"),testCaseList);
        //Ëé∑Âèñtoken
        TypeReference<HashMap<String,String>> typeReference = new TypeReference<HashMap<String,String>>(){};
        HashMap<String, String> login = objectMapper.readValue(new File("src/test/resources/user.json"), typeReference);

        //Ëé∑Âèñtoken

        Response response = given()
                .log().all()
                .contentType("application/json; charset=utf-8")
                .body(login)
                .queryParam("password", "123456")
                .queryParam("username", "admin")
                .when()
                .post("http://127.0.0.1:8085/auth/login")
                .then()
                .log().all()
                .statusCode(200)
                .extract().response();
        String token = response.path("data.accessToken").toString();
        logger.info("ÁôªÂΩïÁöÑtoken:{}", token);
        //‰º†ÂÖ•Êï∞ÊçÆ
        given()
                .log().all()
                .header("Authorization","Bearer "+token)
                .contentType("application/json; charset=utf-8")

                .body(testCaseListStr)
                .when()
                .post("http://127.0.0.1:8085/case")
                .then()
                .log().all();


    }

}
